from functools import reduce
from operator import mul

def mult_list(n):
    if len(n) == 1:
        return n[0]
    else:
        return n[0] * mult_list(n[1:])

def largest_series_product(number, series):
    list_num = [int(i) for i in list(str(number))]
    highest_prod = 0
    for i in range(0,len(list_num)-series+1):
        current_series = list_num[i:i+series]
        product = reduce(mul,current_series)
        if product > highest_prod:
            highest_prod = product
    return highest_prod


